example.code:
$("#position1").position({
	my: "center",
	at: "center",
	of: "#targetElement"
});
$("#position2").position({
	my: "left top",
	at: "left top",
	of: "#targetElement"
});
$("#position3").position({
	my: "right center",
	at: "right bottom",
	of: "#targetElement"
});
$(document).mousemove(function(ev){
	$("#position4").position({
		my: "left bottom",
		of: ev,
		offset: "3 -3",
		collision: "fit"
	});
});
