example.css:
div.green {
	width: 100px;
	height: 80px;
	background: green;
	border: 1px solid black;
	position: relative;
}
div.red {
	margin-top: 10px;
	width: 50px;
	height: 30px;
	background: red;
	border: 1px solid black;
	position: relative;
}
.ui-effects-transfer {
	border: 1px dotted black;
}

example.code:
$( "div" ).click(function() {
	var i = 1 - $( "div" ).index( this );
	$( this ).effect( "transfer", { to: $( "div" ).eq( i ) }, 1000 );
});
